@push('scripts')
    <script>
        $(document).ready(function () {

            let for_edit_form = $("#hiddenSubCategory").val();
            let category_id_for_edit = $("#category").val();
            let for_offerCheckbox_form = $("#hiddenOfferCheckBox").val();
            let for_product_id = $("#product_id").val();
            if (for_edit_form != '') {
                getSubCategories();
            }


            if (for_product_id != '' && for_product_id != 0 && for_offerCheckbox_form != 'product') {
                $('input[name="allowOffer"]').attr('checked', 'checked');
                $(".if_allow_offer").removeClass('d-none');
            }


            $(document).on('change', '#allowOffer', function () {
                let some_value = $('input[name="allowOffer"]').attr('checked', 'checked');
                if ($(this).is(":checked")) {
                    $(".if_allow_offer").removeClass('d-none');
                    $(`#offer_percentages`).attr('required', true).attr('disabled', false);
                    $(`#offer_expirations`).attr('required', true).attr('disabled', false);
                } else {
                    $(".if_allow_offer").addClass('d-none');
                    $(`#offer_percentages`).val('').attr('required', false).attr('disabled', false);
                    $(`#offer_expirations`).attr('required', false).attr('disabled', false);
                }
            });

            $('#stadiumForm').validate({
                showErrors: function (errorMap, errorList) {
                    this.defaultShowErrors();
                    if (!this.numberOfInvalids()) {
                        $(".error_container").hide();
                        $("#error_list").html('');
                    }
                },
                rules: {
                    'description[en]': {
                        required: true,
                        noSpace: true,
                    },
                    'description[ar]': {
                        required: true,
                        noSpace: true,
                    },
                    'name[en]': {
                        required: true,
                        maxlength: 60,
                        noSpace: true,
                    },
                    'name[ar]': {
                        required: true,
                        maxlength: 60,
                        noSpace: true,
                    },
                    category_id: {
                        required: true,
                        noSpace: true
                    },
                    subcategory_id: {
                        required: true,
                        requiredSelect: true
                    },
                    'price': {
                        required: true,
                        digits: true,
                    },
                    product_images: {
                        required: true
                    },
                    images: {
                        required: true
                    },
                    quantity: {
                        required: true,
                        digits: true,
                    }
                },
                messages: {
                    product_images: {
                        required: '{{__("Image is required") }}'
                    },
                    images: {
                        required: '{{__("Image is required") }}'
                    },
                    'description[en]': {
                        required: '{{__("Description in english is required") }}',
                        noSpace: '{{__("Name Field cannot be empty") }}',
                    },
                    'description[ar]': {
                        required: '{{__("Description in arabic is required") }}',
                        noSpace: '{{__("Name Field cannot be empty") }}',
                    },
                    'name[en]': {
                        required: '{{__("Name in english is required") }}',
                        noSpace: '{{__("Name Field cannot be empty") }}',
                    },
                    'name[ar]': {
                        required: '{{__("Name in arabic is required") }}',
                        noSpace: '{{__("Name Field cannot be empty") }}',
                    },
                    category_id: {
                        required: '{{__("Category Field is required") }}'
                    },
                    subcategory_id: {
                        required: '{{__("Sub Category is required") }}'
                    },
                    price: {
                        required: '{{__("Price is required") }}',
                        digits: '{{__("Please Enter Valid Value") }}',
                    },
                    quantity: {
                        required: '{{__("Quantity is required") }}',
                        digits: '{{__("Please Enter Valid Value") }}',
                    }
                },
                errorElement: 'li',
                errorLabelContainer: '#error_list',
                highlight: function (element) {
                    $(".error_container").show();
                }
            });

            $.validator.addMethod("noSpace", function (value, element) {
                return this.optional(element) || value === "NA" ||
                    value.match(/\S/);
            }, "{{__('This field cannot be empty')}}");

            $.validator.addMethod("requiredSelect", function (element) {
                return ($("#subcategory_id").val() != '0');
            }, "You must select an option.");

            $.validator.addMethod("digits", function (value, element) {
                return this.optional(element) || /[+-]?([0-9]*[.])?[0-9]+$/i.test(value);
            }, "{{__('This field should be valid value')}}");

            $("#formSubmit").click(function (e) {
                e.preventDefault();
                if ($("#stadiumForm").valid()) {
                    $(this).prop('disabled', true);
                    $("#stadiumForm").submit();
                }
            });

            $('#category_select').change(function () {
                getSubCategories();
            });

            function getSubCategories() {
                // debugger;
                let category_id = $('#category_select').val();
                let old_sub_category_id = $("#hiddenSubCategory").val();
                let selected = '';
                if (category_id != '' && category_id != undefined && category_id != 0) {
                    let urls = window.Laravel.apiUrl + 'subCategories';
                    $.ajax({
                        url: urls,
                        type: 'GET',
                        data: ({
                            category_id: category_id
                        }),
                        success: function (response, status) {
                            if (status) {
                                let sub_category_options = '<option value=""></option>';
                                response.data.collection.forEach(function (val) {
                                    selected = '';
                                    if (old_sub_category_id == val.id) {
                                        selected = 'selected';
                                    }
                                    sub_category_options += '<option value="' + val.id + '" ' + selected +
                                        '> ' + jsTranslate(val.name) + '</option>';
                                });
                                $('#sub-category').html(sub_category_options);
                                $('#sub-category').change();
                            }
                        }
                    });
                }
            }
        });
    </script>
@endpush
